mapper.select:
select user_id
from users
where user_no = #{userNo}

select count(*) from content join board using(content_no) where board_type = 1
and menu_no = #{menuNo}
and content_title like '%${search}%'

select count(*) from (select c.content_no, c.content_title, c.create_date, c.modify_date, c.content_status, c.delete_status, c.user_no, c.md_recommendation, m.menu_name, LISTAGG(cat.category_name, ',') WITHIN GROUP (ORDER BY cat.category_name) AS category_name from content c left join content_category cc on c.content_no = cc.content_no left join category cat on cc.category_no = cat.category_no left join menu m on cat.menu_no = m.menu_no where not exists ( select 1 FROM board b where c.content_no = b.content_no )
and cat.menu_no = #{menuNo}
group by c.content_no, c.content_title, c.create_date, c.modify_date, c.content_status, c.delete_status, c.user_no, c.md_recommendation, m.menu_name)

select count(*) from content join board using(content_no) where board_type = 2
and menu_no = #{menuNo}
and (content_title like '%${search}%' or content_detail like '%${search}%')

mapper.update:
update users
set status = #{status}
where user_no = #{userNo}

update content
set content_status = #{contentStatus}, delete_status = #{deleteStatus}
where content_no = #{contentNo}

update content
set content_status = #{contentStatus}, delete_status = #{deleteStatus}
where content_no = #{contentNo}

update content
set content_status = #{contentStatus}, delete_status = #{deleteStatus}
where content_no = #{contentNo}
