mapper.select:
SELECT COUNT(*) FROM content JOIN board ON content.content_no = board.content_no WHERE board_type = 1
AND menu_no = #{menuNo}
AND content_title LIKE '%' || #{search} || '%'

SELECT COUNT(*) FROM ( SELECT c.content_no, c.content_title, c.create_date, c.modify_date, c.content_status, c.delete_status, c.user_no, c.md_recommendation, m.menu_name, STRING_AGG(cat.category_name, ',' ORDER BY cat.category_name) AS category_name FROM content c LEFT JOIN content_category cc ON c.content_no = cc.content_no LEFT JOIN category cat ON cc.category_no = cat.category_no LEFT JOIN menu m ON cat.menu_no = m.menu_no WHERE NOT EXISTS ( SELECT 1 FROM board b WHERE c.content_no = b.content_no )
AND cat.menu_no = #{menuNo}
GROUP BY c.content_no, c.content_title, c.create_date, c.modify_date, c.content_status, c.delete_status, c.user_no, c.md_recommendation, m.menu_name ) AS temp

SELECT COUNT(*) FROM content JOIN board ON content.content_no = board.content_no WHERE board_type = 2
AND menu_no = #{menuNo}
AND (content_title LIKE '%' || #{search} || '%' OR content_detail LIKE '%' || #{search} || '%' )

mapper.update:
UPDATE users
SET status = #{status}
WHERE user_no = #{userNo}

UPDATE content
SET content_status = #{contentStatus}, delete_status = #{deleteStatus}
WHERE content_no = #{contentNo}

UPDATE content
SET content_status = #{contentStatus}, delete_status = #{deleteStatus}
WHERE content_no = #{contentNo}

UPDATE content
SET content_status = #{contentStatus}, delete_status = #{deleteStatus}
WHERE content_no = #{contentNo}

mapper.insert:
INSERT INTO reply (
	reply_no, reply_detail, create_date, modify_date, reply_status, content_no, user_no
) VALUES (
			 nextval('reply_seq'), #{replyDetail}, current_timestamp, current_timestamp, 'Y', #{contentNo}, #{userNo}
		 )
