mapper.insert:
INSERT INTO BOARD (
CONTENT_NO,
BOARD_TYPE,
MENU_NO
) VALUES (
#{contentNo},
1,
#{menuNo}
)

mapper.update:
UPDATE BOARD
SET
MENU_NO = #{menuNo}
WHERE
CONTENT_NO = #{contentNo}

mapper.select:
SELECT REPLY_DETAIL
FROM REPLY
WHERE CONTENT_NO = #{contentNo}

select user_id
from users
where user_no = #{userNo}

mapper.update:
update content
set views = views+1
where content_no = #{bId}

UPDATE content
SET content_title = #{contentTitle},
	modify_date = SYSDATE,
	content_detail = #{contentDetail}
WHERE content_no = #{contentNo}

update board
set menu_no = #{menuNo}
where content_no = #{contentNo}

update content
set content_status = 'N'
where content_no =#{bId}
